class Helpers
{
    public static function getSettingsData()
    {
        $data = AseriaManagement::first();
        if (isset($data['data'])) {
            unset($data['data']['created_at'], $data['data']['updated_at'], $data['data']['id']);
            return $data['data'];
        }
        return $data;
    }

    public static function languages()
    {
        return Language::get();
    }

    public static function settings()
    {
        $setting = collect(Setting::first());
        return $setting;
    }

    public static function locations()
    {
        return Location::get();
    }


    public static function invoicePrintSettings()
    {
        return InvoicePrinting::first();
    }

    public static function currentLanguage()
    {
        return Language::where('locale', app()->getLocale())->first();
    }

