$(document).ready(function(){const i=document.getElementById("create-z-rep");if(i){i.addEventListener("submit",t=>{t.preventDefault();const e=document.getElementsByClassName("disable-after-submit");for(let s of e)s.setAttribute("disabled","disabled");return i.submit(),!1});let n=document.getElementsByClassName("disabled-by-default");for(let t of n)t.removeAttribute("disabled")}$(".saldo-ui").each((n,t)=>{let e={currency:"",currency_left:!1};if(t.classList.contains("display-text")){const s=t.getAttribute("currency-symbol");s&&(e.currency=s);const r=t.getAttribute("currency-left");r&&(e.currency_left=r==1,t.textContent=e.currency_left?e.currency+" 0.00":"0.00 "+e.currency)}t.keypadEvent=s=>{const r=s.steppedValue.split(".");r.length>1&&(s.steppedValue=r[0]+"."+r[1].substr(0,2));let c=isNaN(parseFloat(s.steppedValue))?0:parseFloat(s.steppedValue);if(t.classList.contains("display-text")){c=c.toFixed(2);let u=c+" "+e.currency;e.currency_left&&(u=e.currency+" "+c),t.textContent=u}t.classList.contains("input")&&(t.value=c)}})});
